{% for rr in DEFN_NODE_ROLES['RR'] %}
neighbor {{DEFN_LOOP_UNDERLAY[rr]}} inherit peer-session UNDERLAY-SPINE-EVPN-PEER-SESSION-POLICY
{% endfor %}
{% endif %}
!
{# ============================= #}
{# Address-Family: l2vpn evpn    #}
{# ============================= #}
address-family ipv4
maximum-paths ibgp {{ DEFN_NODE_ROLES['SPINE'] | length }}
exit-address-family
