{% if __device.hostname in DEFN_NODE_ROLES['BORDER'] %}
neighbor {{DEFN_LOOP_UNDERLAY[rr]}} next-hop-self
{% endif %}
{% endfor %}
{% endif %}
exit-address-family
!


