{% include "EVPN/DEFN-VRF" %}
{% include "EVPN/DEFN-ROLES" %}
{% include "EVPN/DEFN-L3OUT" %}

{% if vrf_list and FABRIC_BGP_ASN is not none and __device.hostname is not none %}
{% for vrf in vrf_list %}
{% for l3out in DEFN_L3OUT %}
      {% if l3out.vrf == vrf and l3out.node == __device.hostname %}
        router bgp {{FABRIC_BGP_ASN}}
              address-family ipv4 vrf {{l3out.vrf}}
              advertise l2vpn evpn
              {% for aggregate in l3out.aggregate %}
              aggregate-address {{aggregate}} summary-only
              {% endfor %}
              redistribute static
              redistribute connected
              exit-address-family
        {% for interface,params in l3out.interfaces.items() %}
            !
            interface {{interface}}
             encapsulation dot1Q {{params.vlan}}
             vrf forwarding {{l3out.vrf}}
             ip address {{params.ipaddr}}
             ip pim sparse-mode
            !
            !
            router bgp {{FABRIC_BGP_ASN}}
            address-family ipv4 vrf {{l3out.vrf}}
            neighbor {{params.neighbour}} remote-as {{l3out.neighbour_asn}}
            neighbor {{params.neighbour}} activate
            exit-address-family
        {% endfor %}
      {% endif %}
{% endfor %}
{% endfor %}
{% endif %}

